import React, { useState, useEffect } from "react";

export default function SatVeraApp() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("inicio");
  const [time, setTime] = useState(new Date());

  const [nombre, setNombre] = useState("");
  const [direccion, setDireccion] = useState("");
  const [telefono, setTelefono] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [tecnico, setTecnico] = useState("");
  const [valor, setValor] = useState("");
  const [busqueda, setBusqueda] = useState("");

  const [tecnicos, setTecnicos] = useState(() => {
    const data = localStorage.getItem("tecnicos");
    return data ? JSON.parse(data) : [];
  });
  const [nuevoTecnico, setNuevoTecnico] = useState("");
  const [telefonoTecnico, setTelefonoTecnico] = useState("");

  const [servicios, setServicios] = useState(() => {
    const data = localStorage.getItem("servicios");
    return data ? JSON.parse(data) : [];
  });

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const handleLogin = () => {
    if (email === "admin@satvera.cl" && password === "1234") {
      setLoggedIn(true);
      setError("");
    } else {
      setError("Usuario o contraseña incorrectos.");
    }
  };

  const handleGuardarServicio = () => {
    const tecnicoSel = tecnicos.find(t => t.nombre === tecnico);
    if (!tecnicoSel) return alert("Selecciona un técnico válido.");
    const nuevo = {
      nombre,
      direccion,
      telefono,
      descripcion,
      tecnico,
      telefonoTecnico: tecnicoSel.telefono,
      valor,
      fecha: new Date().toLocaleString()
    };
    const actualizados = [...servicios, nuevo];
    setServicios(actualizados);
    localStorage.setItem("servicios", JSON.stringify(actualizados));
    setNombre(""); setDireccion(""); setTelefono(""); setDescripcion(""); setTecnico(""); setValor("");
    alert("Servicio guardado correctamente.");
  };

  const handleAgregarTecnico = () => {
    if (nuevoTecnico && telefonoTecnico) {
      const actualizado = [...tecnicos, { nombre: nuevoTecnico, telefono: telefonoTecnico }];
      setTecnicos(actualizado);
      localStorage.setItem("tecnicos", JSON.stringify(actualizado));
      setNuevoTecnico(""); setTelefonoTecnico("");
    }
  };

  const compartirConTecnico = (serv) => {
    const mensaje = `Nuevo servicio:
Cliente: ${serv.nombre}
Dirección: ${serv.direccion}
Teléfono: ${serv.telefono}
Descripción: ${serv.descripcion}
Valor: ${serv.valor}
Fecha: ${serv.fecha}`;
    window.open(`https://wa.me/${serv.telefonoTecnico.replace(/[^0-9]/g, "")}?text=${encodeURIComponent(mensaje)}`, "_blank");
  };

  const compartirConCliente = (serv) => {
    const mensaje = `Hola ${serv.nombre}, el técnico que lo atenderá es ${serv.tecnico}.
Hora: ${new Date().toLocaleTimeString()}.
Teléfono técnico: ${serv.telefonoTecnico}`;
    window.open(`https://wa.me/${serv.telefono.replace(/[^0-9]/g, "")}?text=${encodeURIComponent(mensaje)}`, "_blank");
  };

  const renderTab = () => {
    if (activeTab === "inicio") {
      return <div className="text-white text-center text-xl">{time.toLocaleString()}</div>;
    } else if (activeTab === "nuevo") {
      return (
        <div className="bg-white rounded-xl shadow p-6 space-y-4">
          <input className="w-full p-2 border rounded" placeholder="Nombre" value={nombre} onChange={e => setNombre(e.target.value)} />
          <input className="w-full p-2 border rounded" placeholder="Dirección" value={direccion} onChange={e => setDireccion(e.target.value)} />
          <input className="w-full p-2 border rounded" placeholder="Teléfono" value={telefono} onChange={e => setTelefono(e.target.value)} />
          <input className="w-full p-2 border rounded" placeholder="Descripción" value={descripcion} onChange={e => setDescripcion(e.target.value)} />
          <select className="w-full p-2 border rounded" value={tecnico} onChange={e => setTecnico(e.target.value)}>
            <option value="">Selecciona técnico</option>
            {tecnicos.map((t, i) => <option key={i}>{t.nombre}</option>)}
          </select>
          <input className="w-full p-2 border rounded" placeholder="Valor" value={valor} onChange={e => setValor(e.target.value)} />
          <button onClick={handleGuardarServicio} className="w-full bg-green-600 text-white p-2 rounded hover:bg-green-700">Guardar Servicio</button>
        </div>
      );
    } else if (activeTab === "servicios") {
      return (
        <div className="bg-white rounded-xl shadow p-6 space-y-4">
          <input className="w-full p-2 border rounded" placeholder="Buscar..." value={busqueda} onChange={e => setBusqueda(e.target.value)} />
          {servicios.filter(s => s.nombre.includes(busqueda)).map((s, i) => (
            <div key={i} className="border p-4 rounded bg-gray-50">
              <p><b>Cliente:</b> {s.nombre}</p>
              <p><b>Dirección:</b> {s.direccion}</p>
              <p><b>Teléfono:</b> {s.telefono}</p>
              <p><b>Descripción:</b> {s.descripcion}</p>
              <p><b>Técnico:</b> {s.tecnico}</p>
              <p><b>Tel. Técnico:</b> {s.telefonoTecnico}</p>
              <p><b>Valor:</b> {s.valor}</p>
              <p><b>Fecha:</b> {s.fecha}</p>
              <div className="flex gap-2 mt-2">
                <button onClick={() => compartirConTecnico(s)} className="bg-blue-600 text-white px-3 py-1 rounded">Compartir Técnico</button>
                <button onClick={() => compartirConCliente(s)} className="bg-yellow-500 text-white px-3 py-1 rounded">Compartir Cliente</button>
              </div>
            </div>
          ))}
        </div>
      );
    } else if (activeTab === "tecnicos") {
      return (
        <div className="bg-white rounded-xl shadow p-6 space-y-4">
          <input className="w-full p-2 border rounded" placeholder="Nombre del técnico" value={nuevoTecnico} onChange={e => setNuevoTecnico(e.target.value)} />
          <input className="w-full p-2 border rounded" placeholder="Teléfono del técnico" value={telefonoTecnico} onChange={e => setTelefonoTecnico(e.target.value)} />
          <button onClick={handleAgregarTecnico} className="w-full bg-yellow-500 text-white p-2 rounded hover:bg-yellow-600">Agregar Técnico</button>
          <ul className="space-y-2">
            {tecnicos.map((t, i) => (
              <li key={i} className="flex justify-between items-center border p-2 rounded">{t.nombre} - {t.telefono}</li>
            ))}
          </ul>
        </div>
      );
    }
  };

  if (!loggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-500 via-green-400 to-yellow-300">
        <div className="bg-white p-6 rounded-xl shadow-xl max-w-sm w-full">
          <h2 className="text-xl font-bold text-center text-blue-700 mb-4">Iniciar sesión</h2>
          <input className="w-full p-2 border rounded mb-3" placeholder="Correo" value={email} onChange={e => setEmail(e.target.value)} />
          <input className="w-full p-2 border rounded mb-3" type="password" placeholder="Contraseña" value={password} onChange={e => setPassword(e.target.value)} />
          {error && <p className="text-red-600 text-sm">{error}</p>}
          <button onClick={handleLogin} className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700 mt-2">Entrar</button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-green-400 to-yellow-300 p-6">
      <h1 className="text-3xl font-bold text-center text-white mb-6">SAT VERA</h1>
      <div className="flex justify-center gap-2 flex-wrap mb-6">
        <button onClick={() => setActiveTab("inicio")} className={activeTab === "inicio" ? "bg-blue-600 text-white px-4 py-2 rounded" : "bg-white px-4 py-2 rounded"}>Inicio</button>
        <button onClick={() => setActiveTab("nuevo")} className={activeTab === "nuevo" ? "bg-green-600 text-white px-4 py-2 rounded" : "bg-white px-4 py-2 rounded"}>Nuevo Servicio</button>
        <button onClick={() => setActiveTab("servicios")} className={activeTab === "servicios" ? "bg-blue-500 text-white px-4 py-2 rounded" : "bg-white px-4 py-2 rounded"}>Servicios</button>
        <button onClick={() => setActiveTab("tecnicos")} className={activeTab === "tecnicos" ? "bg-yellow-500 text-white px-4 py-2 rounded" : "bg-white px-4 py-2 rounded"}>Técnicos</button>
      </div>
      {renderTab()}
    </div>
  );
}
