import React, { useState, useEffect } from "react";

export default function SatVeraApp() {
  // Estados de autenticación y otros
  const [loggedIn, setLoggedIn] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("inicio");
  const [time, setTime] = useState(new Date());

  // Estado para recordar el correo
  const [rememberEmail, setRememberEmail] = useState(false);

  // Estados para formulario de nuevo servicio
  const [nombre, setNombre] = useState("");
  const [direccion, setDireccion] = useState("");
  const [telefono, setTelefono] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [tecnico, setTecnico] = useState("");
  const [valor, setValor] = useState(""); // Se guardará como string formateado (por ejemplo "40.000")
  const [fechaAtencion, setFechaAtencion] = useState("");
  const [busqueda, setBusqueda] = useState("");

  // Estados para técnicos y servicios
  const [tecnicos, setTecnicos] = useState(() => {
    const data = localStorage.getItem("tecnicos");
    return data ? JSON.parse(data) : [];
  });
  const [nuevoTecnico, setNuevoTecnico] = useState("");
  const [telefonoTecnico, setTelefonoTecnico] = useState("");

  const [servicios, setServicios] = useState(() => {
    const data = localStorage.getItem("servicios");
    return data ? JSON.parse(data) : [];
  });

  // Estados para edición de servicios y técnicos
  const [editingIndex, setEditingIndex] = useState(null);
  const [editServiceData, setEditServiceData] = useState(null);
  const [editingTecnicoIndex, setEditingTecnicoIndex] = useState(null);
  const [editTecnicoData, setEditTecnicoData] = useState(null);

  // Estados para Finanzas: rango de fechas
  const [financeFrom, setFinanceFrom] = useState("");
  const [financeTo, setFinanceTo] = useState("");

  // Al iniciar el componente, actualizar el reloj y cargar el correo recordado (si existe)
  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    const storedEmail = localStorage.getItem("rememberedEmail");
    if (storedEmail) {
      setEmail(storedEmail);
      setRememberEmail(true);
    }
    return () => clearInterval(interval);
  }, []);

  // Función para formatear números a formato CLP sin decimales (por ejemplo: 40000 -> "40.000")
  const formatCurrency = (num) => {
    return num.toLocaleString("es-CL", { maximumFractionDigits: 0 });
  };

  // Función que al salir del input de "valor" formatea el valor ingresado
  const handleValorBlur = () => {
    if (valor === "") return;
    // Eliminar cualquier caracter que no sea dígito para obtener el número crudo
    const numericValue = parseInt(valor.replace(/\D/g, ""), 10);
    if (!isNaN(numericValue)) {
      setValor(formatCurrency(numericValue));
    }
  };

  // Manejo del login: contraseña "1234"
  const handleLogin = () => {
    if (email === "admin@satvera.cl" && password === "1234") {
      setLoggedIn(true);
      setError("");
      // Si se marcó recordar correo, se guarda en localStorage, de lo contrario se elimina.
      if (rememberEmail) {
        localStorage.setItem("rememberedEmail", email);
      } else {
        localStorage.removeItem("rememberedEmail");
      }
    } else {
      setError("Usuario o contraseña incorrectos.");
    }
  };

  // Guardar nuevo servicio
  const handleGuardarServicio = () => {
    const tecnicoSel = tecnicos.find((t) => t.nombre === tecnico);
    if (!tecnicoSel) return alert("Selecciona un técnico válido.");
    const nuevo = {
      nombre,
      direccion,
      telefono,
      descripcion,
      tecnico,
      telefonoTecnico: tecnicoSel.telefono,
      valor, // Se almacena con el formato, por ejemplo "40.000"
      fechaAtencion,
      fecha: new Date().toLocaleString()
    };
    const actualizados = [...servicios, nuevo];
    setServicios(actualizados);
    localStorage.setItem("servicios", JSON.stringify(actualizados));
    // Reiniciamos campos
    setNombre("");
    setDireccion("");
    setTelefono("");
    setDescripcion("");
    setTecnico("");
    setValor("");
    setFechaAtencion("");
    alert("Servicio guardado correctamente.");
  };

  const handleAgregarTecnico = () => {
    if (nuevoTecnico && telefonoTecnico) {
      const actualizado = [...tecnicos, { nombre: nuevoTecnico, telefono: telefonoTecnico }];
      setTecnicos(actualizado);
      localStorage.setItem("tecnicos", JSON.stringify(actualizado));
      setNuevoTecnico("");
      setTelefonoTecnico("");
    }
  };

  const compartirConTecnico = (serv) => {
    const mensaje = `Nuevo servicio:
Cliente: ${serv.nombre}
Dirección: ${serv.direccion}
Teléfono: ${serv.telefono}
Descripción: ${serv.descripcion}
Valor: ${serv.valor}
Fecha: ${serv.fecha}`;
    window.open(`https://wa.me/${serv.telefonoTecnico.replace(/[^0-9]/g, "")}?text=${encodeURIComponent(mensaje)}`, "_blank");
  };

  // Función modificada para compartir con el cliente
  const compartirConCliente = (serv) => {
    const horaAtencion = serv.fechaAtencion ? serv.fechaAtencion.slice(11) : "";
    const mensaje = `Hola ${serv.nombre}, queremos informar desde Servicios Vera que el técnico ${serv.tecnico} irá a su domicilio a las ${horaAtencion}. Favor confirmar información.`;
    window.open(`https://wa.me/${serv.telefono.replace(/[^0-9]/g, "")}?text=${encodeURIComponent(mensaje)}`, "_blank");
  };

  // Funciones para edición de servicios
  const handleStartEdit = (index) => {
    const enteredPassword = prompt("Ingrese la contraseña para editar");
    if (enteredPassword !== "1234") {
      alert("Contraseña incorrecta.");
      return;
    }
    setEditingIndex(index);
    setEditServiceData({ ...servicios[index] });
  };

  const handleEditChange = (field, value) => {
    setEditServiceData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSaveEdit = () => {
    const tecnicoSel = tecnicos.find((t) => t.nombre === editServiceData.tecnico);
    if (!tecnicoSel) {
      alert("Selecciona un técnico válido.");
      return;
    }
    const updatedService = {
      ...editServiceData,
      telefonoTecnico: tecnicoSel.telefono
    };
    const updatedServices = servicios.map((serv, i) =>
      i === editingIndex ? updatedService : serv
    );
    setServicios(updatedServices);
    localStorage.setItem("servicios", JSON.stringify(updatedServices));
    setEditingIndex(null);
    setEditServiceData(null);
    alert("Edición guardada correctamente.");
  };

  const handleCancelEdit = () => {
    setEditingIndex(null);
    setEditServiceData(null);
  };

  const handleDeleteService = (index) => {
    const enteredPassword = prompt("Ingrese la contraseña para eliminar");
    if (enteredPassword !== "1234") {
      alert("Contraseña incorrecta.");
      return;
    }
    const updatedServices = servicios.filter((_, i) => i !== index);
    setServicios(updatedServices);
    localStorage.setItem("servicios", JSON.stringify(updatedServices));
    alert("Servicio eliminado.");
  };

  // Funciones para edición de técnicos
  const handleStartEditTecnico = (index) => {
    const enteredPassword = prompt("Ingrese la contraseña para editar técnico");
    if (enteredPassword !== "1234") {
      alert("Contraseña incorrecta.");
      return;
    }
    setEditingTecnicoIndex(index);
    setEditTecnicoData({ ...tecnicos[index] });
  };

  const handleEditTecnicoChange = (field, value) => {
    setEditTecnicoData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSaveEditTecnico = () => {
    const updatedTecnicos = tecnicos.map((tec, i) =>
      i === editingTecnicoIndex ? editTecnicoData : tec
    );
    setTecnicos(updatedTecnicos);
    localStorage.setItem("tecnicos", JSON.stringify(updatedTecnicos));
    setEditingTecnicoIndex(null);
    setEditTecnicoData(null);
    alert("Técnico editado correctamente.");
  };

  const handleCancelEditTecnico = () => {
    setEditingTecnicoIndex(null);
    setEditTecnicoData(null);
  };

  const handleDeleteTecnico = (index) => {
    const enteredPassword = prompt("Ingrese la contraseña para eliminar técnico");
    if (enteredPassword !== "1234") {
      alert("Contraseña incorrecta.");
      return;
    }
    const updatedTecnicos = tecnicos.filter((_, i) => i !== index);
    setTecnicos(updatedTecnicos);
    localStorage.setItem("tecnicos", JSON.stringify(updatedTecnicos));
    alert("Técnico eliminado.");
  };

  // Renderizado del inicio con widget de calendario/reloj y recuadros de servicios
  const renderInicioTab = () => {
    const hoy = new Date().toISOString().split("T")[0];
    const serviciosHoy = servicios.filter(
      (s) => s.fechaAtencion && s.fechaAtencion.slice(0, 10) === hoy
    );
    const serviciosOtros = servicios.filter(
      (s) => !s.fechaAtencion || s.fechaAtencion.slice(0, 10) !== hoy
    );
    return (
      <div className="space-y-4">
        {/* Widget de calendario y reloj */}
        <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center">
          <div className="text-3xl font-bold">{time.toLocaleTimeString()}</div>
          <div className="text-xl">{time.toLocaleDateString()}</div>
        </div>
        {/* Recuadro de servicios para hoy */}
        <div className="bg-white rounded-xl shadow p-6">
          <h2 className="text-xl font-bold mb-2">Servicios para hoy</h2>
          {serviciosHoy.length === 0 ? (
            <p>No hay servicios para hoy.</p>
          ) : (
            serviciosHoy.map((s, i) => (
              <div key={i} className="border p-2 rounded mb-2">
                <p>
                  <b>Cliente:</b> {s.nombre}
                </p>
                <p>
                  <b>Atención:</b> {s.fechaAtencion}
                </p>
              </div>
            ))
          )}
        </div>
        {/* Recuadro de servicios para otros días */}
        <div className="bg-white rounded-xl shadow p-6">
          <h2 className="text-xl font-bold mb-2">Servicios para otros días</h2>
          {serviciosOtros.length === 0 ? (
            <p>No hay servicios para otros días.</p>
          ) : (
            serviciosOtros.map((s, i) => (
              <div key={i} className="border p-2 rounded mb-2">
                <p>
                  <b>Cliente:</b> {s.nombre}
                </p>
                <p>
                  <b>Atención:</b> {s.fechaAtencion}
                </p>
              </div>
            ))
          )}
        </div>
      </div>
    );
  };

  // Renderizado de la pestaña Finanzas
  const renderFinanzasTab = () => {
    // Filtra los servicios dentro del rango definido
    const filteredServices = servicios.filter((s) => {
      if (!s.fechaAtencion) return false;
      const serviceDate = s.fechaAtencion.slice(0, 10);
      if (financeFrom && serviceDate < financeFrom) return false;
      if (financeTo && serviceDate > financeTo) return false;
      return true;
    });
    // Al convertir, eliminamos el punto (separador de miles) para obtener el valor numérico correcto
    const totalSAT = filteredServices.reduce(
      (sum, s) =>
        sum + (parseFloat(s.valor.replace(/\./g, "")) || 0),
      0
    );
    const totalesTecnicos = {};
    filteredServices.forEach((s) => {
      const tech = s.tecnico;
      const val = parseFloat(s.valor.replace(/\./g, "")) || 0;
      if (tech) {
        totalesTecnicos[tech] = (totalesTecnicos[tech] || 0) + val;
      }
    });
    return (
      <div className="bg-white rounded-xl shadow p-6 space-y-4">
        <h2 className="text-xl font-bold mb-2">Finanzas</h2>
        <div className="flex gap-2">
          <input
            type="date"
            className="p-2 border rounded"
            value={financeFrom}
            onChange={(e) => setFinanceFrom(e.target.value)}
            placeholder="Desde"
          />
          <input
            type="date"
            className="p-2 border rounded"
            value={financeTo}
            onChange={(e) => setFinanceTo(e.target.value)}
            placeholder="Hasta"
          />
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-lg font-bold">
            Total SAT: ${formatCurrency(totalSAT)} CLP
          </p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <h3 className="font-bold mb-2">Finanzas por Técnico:</h3>
          {Object.keys(totalesTecnicos).length === 0 ? (
            <p>No hay datos para los técnicos.</p>
          ) : (
            Object.entries(totalesTecnicos).map(([tech, total], i) => (
              <p key={i}>
                <b>{tech}</b>: ${formatCurrency(total)} CLP
              </p>
            ))
          )}
        </div>
      </div>
    );
  };

  const renderTab = () => {
    if (activeTab === "inicio") {
      return renderInicioTab();
    } else if (activeTab === "nuevo") {
      return (
        <div className="bg-white rounded-xl shadow p-6 space-y-4">
          <input
            className="w-full p-2 border rounded"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
          <input
            className="w-full p-2 border rounded"
            placeholder="Dirección"
            value={direccion}
            onChange={(e) => setDireccion(e.target.value)}
          />
          <input
            className="w-full p-2 border rounded"
            placeholder="Teléfono"
            value={telefono}
            onChange={(e) => setTelefono(e.target.value)}
          />
          <input
            className="w-full p-2 border rounded"
            placeholder="Descripción"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
          <select
            className="w-full p-2 border rounded"
            value={tecnico}
            onChange={(e) => setTecnico(e.target.value)}
          >
            <option value="">Selecciona técnico</option>
            {tecnicos.map((t, i) => (
              <option key={i} value={t.nombre}>
                {t.nombre}
              </option>
            ))}
          </select>
          <input
            className="w-full p-2 border rounded"
            placeholder="Valor (CLP)"
            value={valor}
            // Permitir solo dígitos al escribir
            onChange={(e) =>
              setValor(e.target.value.replace(/\D/g, ""))
            }
            // Al salir del input, formatear el número
            onBlur={handleValorBlur}
          />
          <input
            className="w-full p-2 border rounded"
            type="datetime-local"
            placeholder="Fecha y hora de atención"
            value={fechaAtencion}
            onChange={(e) => setFechaAtencion(e.target.value)}
          />
          <button
            onClick={handleGuardarServicio}
            className="w-full bg-green-600 text-white p-2 rounded hover:bg-green-700"
          >
            Guardar Servicio
          </button>
        </div>
      );
    } else if (activeTab === "servicios") {
      return (
        <div className="bg-white rounded-xl shadow p-6 space-y-4">
          <input
            className="w-full p-2 border rounded"
            placeholder="Buscar..."
            value={busqueda}
            onChange={(e) => setBusqueda(e.target.value)}
          />
          {servicios
            .filter((s) => s.nombre.includes(busqueda))
            .map((s, i) => (
              <div key={i} className="border p-4 rounded bg-gray-50">
                {editingIndex === i && editServiceData ? (
                  <div className="space-y-4">
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Nombre"
                      value={editServiceData.nombre}
                      onChange={(e) =>
                        handleEditChange("nombre", e.target.value)
                      }
                    />
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Dirección"
                      value={editServiceData.direccion}
                      onChange={(e) =>
                        handleEditChange("direccion", e.target.value)
                      }
                    />
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Teléfono"
                      value={editServiceData.telefono}
                      onChange={(e) =>
                        handleEditChange("telefono", e.target.value)
                      }
                    />
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Descripción"
                      value={editServiceData.descripcion}
                      onChange={(e) =>
                        handleEditChange("descripcion", e.target.value)
                      }
                    />
                    <select
                      className="w-full p-2 border rounded"
                      value={editServiceData.tecnico}
                      onChange={(e) =>
                        handleEditChange("tecnico", e.target.value)
                      }
                    >
                      <option value="">Selecciona técnico</option>
                      {tecnicos.map((t, j) => (
                        <option key={j} value={t.nombre}>
                          {t.nombre}
                        </option>
                      ))}
                    </select>
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Valor"
                      value={editServiceData.valor}
                      onChange={(e) =>
                        handleEditChange("valor", e.target.value)
                      }
                    />
                    <input
                      className="w-full p-2 border rounded"
                      type="datetime-local"
                      placeholder="Fecha y hora de atención"
                      value={editServiceData.fechaAtencion}
                      onChange={(e) =>
                        handleEditChange("fechaAtencion", e.target.value)
                      }
                    />
                    <div className="flex gap-2 mt-2">
                      <button
                        onClick={handleSaveEdit}
                        className="bg-green-600 text-white px-3 py-1 rounded"
                      >
                        Guardar Edición
                      </button>
                      <button
                        onClick={handleCancelEdit}
                        className="bg-gray-500 text-white px-3 py-1 rounded"
                      >
                        Cancelar
                      </button>
                    </div>
                  </div>
                ) : (
                  <div>
                    <p>
                      <b>Cliente:</b> {s.nombre}
                    </p>
                    <p>
                      <b>Dirección:</b> {s.direccion}
                    </p>
                    <p>
                      <b>Teléfono:</b> {s.telefono}
                    </p>
                    <p>
                      <b>Descripción:</b> {s.descripcion}
                    </p>
                    <p>
                      <b>Técnico:</b> {s.tecnico}
                    </p>
                    <p>
                      <b>Tel. Técnico:</b> {s.telefonoTecnico}
                    </p>
                    <p>
                      <b>Valor:</b> {s.valor}
                    </p>
                    <p>
                      <b>Fecha y hora de atención:</b> {s.fechaAtencion}
                    </p>
                    <p>
                      <b>Fecha de ingreso:</b> {s.fecha}
                    </p>
                    <div className="flex gap-2 mt-2">
                      <button
                        onClick={() => compartirConTecnico(s)}
                        className="bg-blue-600 text-white px-3 py-1 rounded"
                      >
                        Compartir Técnico
                      </button>
                      <button
                        onClick={() => compartirConCliente(s)}
                        className="bg-yellow-500 text-white px-3 py-1 rounded"
                      >
                        Compartir Cliente
                      </button>
                      <button
                        onClick={() => handleStartEdit(i)}
                        className="bg-green-600 text-white px-3 py-1 rounded"
                      >
                        Editar
                      </button>
                      <button
                        onClick={() => handleDeleteService(i)}
                        className="bg-red-600 text-white px-3 py-1 rounded"
                      >
                        Eliminar
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))}
        </div>
      );
    } else if (activeTab === "tecnicos") {
      return (
        <div className="bg-white rounded-xl shadow p-6 space-y-4">
          <div className="space-y-4">
            <input
              className="w-full p-2 border rounded"
              placeholder="Nombre del técnico"
              value={nuevoTecnico}
              onChange={(e) => setNuevoTecnico(e.target.value)}
            />
            <input
              className="w-full p-2 border rounded"
              placeholder="Teléfono del técnico"
              value={telefonoTecnico}
              onChange={(e) => setTelefonoTecnico(e.target.value)}
            />
            <button
              onClick={handleAgregarTecnico}
              className="w-full bg-yellow-500 text-white p-2 rounded hover:bg-yellow-600"
            >
              Agregar Técnico
            </button>
          </div>
          <ul className="space-y-2">
            {tecnicos.map((t, i) => (
              <li key={i} className="border p-2 rounded">
                {editingTecnicoIndex === i && editTecnicoData ? (
                  <div className="space-y-2">
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Nombre"
                      value={editTecnicoData.nombre}
                      onChange={(e) =>
                        handleEditTecnicoChange("nombre", e.target.value)
                      }
                    />
                    <input
                      className="w-full p-2 border rounded"
                      placeholder="Teléfono"
                      value={editTecnicoData.telefono}
                      onChange={(e) =>
                        handleEditTecnicoChange("telefono", e.target.value)
                      }
                    />
                    <div className="flex gap-2">
                      <button
                        onClick={handleSaveEditTecnico}
                        className="bg-green-600 text-white px-3 py-1 rounded"
                      >
                        Guardar
                      </button>
                      <button
                        onClick={handleCancelEditTecnico}
                        className="bg-gray-500 text-white px-3 py-1 rounded"
                      >
                        Cancelar
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="flex justify-between items-center">
                    <span>
                      {t.nombre} - {t.telefono}
                    </span>
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleStartEditTecnico(i)}
                        className="bg-green-600 text-white px-2 py-1 rounded"
                      >
                        Editar Técnico
                      </button>
                      <button
                        onClick={() => handleDeleteTecnico(i)}
                        className="bg-red-600 text-white px-2 py-1 rounded"
                      >
                        Eliminar Técnico
                      </button>
                    </div>
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      );
    } else if (activeTab === "finanzas") {
      return renderFinanzasTab();
    }
  };

  if (!loggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-500 via-green-400 to-yellow-300">
        <div className="bg-white p-6 rounded-xl shadow-xl max-w-sm w-full">
          <h2 className="text-xl font-bold text-center text-blue-700 mb-4">
            Iniciar sesión
          </h2>
          <input
            className="w-full p-2 border rounded mb-3"
            placeholder="Correo"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {/* Checkbox para recordar el correo */}
          <label className="flex items-center mb-3">
            <input
              type="checkbox"
              checked={rememberEmail}
              onChange={(e) => setRememberEmail(e.target.checked)}
            />
            <span className="ml-2 text-sm text-gray-600">
              Recordar correo
            </span>
          </label>
          <input
            className="w-full p-2 border rounded mb-3"
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {error && <p className="text-red-600 text-sm">{error}</p>}
          <button
            onClick={handleLogin}
            className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700 mt-2"
          >
            Entrar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-green-400 to-yellow-300 p-6">
      <h1 className="text-3xl font-bold text-center text-white mb-6">
        SAT VERA
      </h1>
      <div className="flex justify-center gap-2 flex-wrap mb-6">
        <button
          onClick={() => setActiveTab("inicio")}
          className={
            activeTab === "inicio"
              ? "bg-blue-600 text-white px-4 py-2 rounded"
              : "bg-white px-4 py-2 rounded"
          }
        >
          Inicio
        </button>
        <button
          onClick={() => setActiveTab("nuevo")}
          className={
            activeTab === "nuevo"
              ? "bg-green-600 text-white px-4 py-2 rounded"
              : "bg-white px-4 py-2 rounded"
          }
        >
          Nuevo Servicio
        </button>
        <button
          onClick={() => setActiveTab("servicios")}
          className={
            activeTab === "servicios"
              ? "bg-blue-500 text-white px-4 py-2 rounded"
              : "bg-white px-4 py-2 rounded"
          }
        >
          Servicios
        </button>
        <button
          onClick={() => setActiveTab("tecnicos")}
          className={
            activeTab === "tecnicos"
              ? "bg-yellow-500 text-white px-4 py-2 rounded"
              : "bg-white px-4 py-2 rounded"
          }
        >
          Técnicos
        </button>
        <button
          onClick={() => setActiveTab("finanzas")}
          className={
            activeTab === "finanzas"
              ? "bg-purple-600 text-white px-4 py-2 rounded"
              : "bg-white px-4 py-2 rounded"
          }
        >
          Finanzas
        </button>
      </div>
      {/* Envoltura para limitar el ancho máximo a 75% de la pantalla */}
      <div style={{ maxWidth: "75%", margin: "0 auto" }}>
        {renderTab()}
      </div>
    </div>
  );
}
